class PomodoroTimer {
    constructor() {
        this.minutesDisplay = document.getElementById('minutes');
        this.secondsDisplay = document.getElementById('seconds');
        this.startButton = document.getElementById('start');
        this.pauseButton = document.getElementById('pause');
        this.resetButton = document.getElementById('reset');
        this.pomodoroButton = document.getElementById('pomodoro');
        this.shortBreakButton = document.getElementById('shortBreak');
        this.longBreakButton = document.getElementById('longBreak');

        this.timeLeft = 25 * 60; // 25 minutes in seconds
        this.timerId = null;
        this.isRunning = false;

        this.initializeButtons();
        this.updateDisplay();
    }

    initializeButtons() {
        this.startButton.addEventListener('click', () => this.start());
        this.pauseButton.addEventListener('click', () => this.pause());
        this.resetButton.addEventListener('click', () => this.reset());
        
        this.pomodoroButton.addEventListener('click', () => this.setTime(25));
        this.shortBreakButton.addEventListener('click', () => this.setTime(5));
        this.longBreakButton.addEventListener('click', () => this.setTime(15));
    }

    updateDisplay() {
        const minutes = Math.floor(this.timeLeft / 60);
        const seconds = this.timeLeft % 60;
        
        this.minutesDisplay.textContent = minutes.toString().padStart(2, '0');
        this.secondsDisplay.textContent = seconds.toString().padStart(2, '0');
    }

    start() {
        if (!this.isRunning) {
            this.isRunning = true;
            this.timerId = setInterval(() => {
                this.timeLeft--;
                this.updateDisplay();
                
                if (this.timeLeft === 0) {
                    this.playAlarm();
                    this.pause();
                }
            }, 1000);
        }
    }

    pause() {
        this.isRunning = false;
        clearInterval(this.timerId);
    }

    reset() {
        this.pause();
        this.timeLeft = 25 * 60;
        this.updateDisplay();
    }

    setTime(minutes) {
        this.pause();
        this.timeLeft = minutes * 60;
        this.updateDisplay();
        
        // Update active button
        [this.pomodoroButton, this.shortBreakButton, this.longBreakButton].forEach(btn => {
            btn.classList.remove('active');
        });
        
        if (minutes === 25) this.pomodoroButton.classList.add('active');
        else if (minutes === 5) this.shortBreakButton.classList.add('active');
        else if (minutes === 15) this.longBreakButton.classList.add('active');
    }

    playAlarm() {
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
    }
}

// Initialize the timer when the page loads
document.addEventListener('DOMContentLoaded', () => {
    const timer = new PomodoroTimer();
});

// HIGATHER button functionality
const higatherBtn = document.getElementById('higather');
const explosion = document.getElementById('explosion');

higatherBtn.addEventListener('click', () => {
    explosion.classList.add('show');
    
    // Hide the explosion after animation
    setTimeout(() => {
        explosion.classList.remove('show');
    }, 10000); // 10 seconds
}); 